import axios from 'axios';


// Api for sign up 

export const signupUser = async (userData) => {
  const response = await axios.post('http://127.0.0.1:8000/BugUsers/signup/', userData);
  return response.data;
};

// Api for Login in
export const loginUser = async (userData) => {
  const response = await axios.post('http://127.0.0.1:8000/BugUsers/login/', userData);
  return response.data;
};

// This is the api call for search all Projects
const API_BASE_URL = 'http://localhost:8000/Projects';
export const fetchAllProjects = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/allprojects/`);
    return response.data;
  } catch (error) {
    console.error('Error fetching projects data:', error);
    throw error;
  }
};

// This is the api call for search Projects
export const searchProjects = async (query) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/searchproject/`, {
      params: {
        project_name: query
      }
    });
    return response.data;
  } catch (error) {
    console.error('Error searching projects:', error);
    throw error;
  }
};


// This is the api call for search bug 

const apiClient = axios.create({
  baseURL: 'http://localhost:8000',
  headers: {
    Authorization: `Bearer ${localStorage.getItem('authToken')}`,
  },
});

export const searchBugs = (bug_description, bug_project_id) => {
  return apiClient.get('/Bugs/searchbug/', {
    params: {
      bug_description,
      bug_project_id,
    },
  });
};

// This is the Api call for update the bug

const API_BASE_URL2 = 'http://localhost:8000/Bugs';

export const updateBugStatus = async (bugId, status) => {
  try {
    await axios.patch(`${API_BASE_URL2}/update/${bugId}/`, {
      bug_type: status,
    });
    console.log(`Bug ${bugId} status changed to ${status}.`);
    return true;
  } catch (error) {
    console.error(`Error updating bug ${bugId} status to ${status}:`, error);
    return false;
  }
};

// This is the api call for delete bug
export const deleteBug = async (bugId) => {
  try {
    await axios.patch(`${API_BASE_URL2}/update/${bugId}/`, {
      bug_type: "Deleted",
    });
    console.log(`Bug ${bugId} marked as deleted.`);
    return true;
  } catch (error) {
    console.error(`Error deleting bug ${bugId}:`, error);
    return false;
  }
};
