import axios from 'axios';

// Set up the base URL and axios instance with default headers
const API_BASE_URL = 'http://localhost:8000';
const authToken = localStorage.getItem('authToken');

const apiClient = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    Authorization: `Bearer ${authToken}`,
  },
});

// API call for fetching all projects
export const fetchAllProjects = async () => {
  try {
    const response = await apiClient.get('/Projects/allprojects/');
    return response.data;
  } catch (error) {
    console.error('Error fetching projects data:', error);
    throw error;
  }
};

// API call for searching projects
export const searchProjects = async (query) => {
  try {
    const response = await apiClient.get('/Projects/searchproject/', {
      params: {
        project_name: query
      }
    });
    return response.data;
  } catch (error) {
    console.error('Error searching projects:', error);
    throw error;
  }
};

// API call for searching bugs
export const searchBugs = async (bug_description, bug_project_id) => {
  try {
    const response = await apiClient.get('/Bugs/searchbug/', {
      params: {
        bug_description,
        bug_project_id,
      },
    });
    return response.data;
  } catch (error) {
    console.error('Error searching bugs:', error);
    throw error;
  }
};

// API call for updating bug status
export const updateBugStatus = async (bugId, status) => {
  try {
    await apiClient.patch(`/Bugs/update/${bugId}/`, {
      bug_type: status,
    });
    console.log(`Bug ${bugId} status changed to ${status}.`);
    return true;
  } catch (error) {
    console.error(`Error updating bug ${bugId} status to ${status}:`, error);
    return false;
  }
};

// API call for deleting a bug
export const deleteBug = async (bugId) => {
  try {
    await apiClient.patch(`/Bugs/update/${bugId}/`, {
      bug_type: "Deleted",
    });
    console.log(`Bug ${bugId} marked as deleted.`);
    return true;
  } catch (error) {
    console.error(`Error deleting bug ${bugId}:`, error);
    return false;
  }
};
